CREATE TABLE users (id INT NOT NULL AUTO_INCREMENT PRIMARY KEY, modifiedDate TIMESTAMP, createDate DATETIME, username VARCHAR(100), password VARCHAR(20), firstname VARCHAR(100), lastname VARCHAR(100), dob DATETIME, male TINYINT UNSIGNED NULL DEFAULT 1, email VARCHAR(100), mobilePhone VARCHAR(30), homePhone VARCHAR(30), workPhone VARCHAR(30), addressLine1 VARCHAR(100), addressLine2 VARCHAR(100), addressLine3 VARCHAR(100), city VARCHAR(100), county VARCHAR(100), postCode VARCHAR(10), country VARCHAR(2));